var setEnv = require('../src/setEnv');
var assert = require("assert");



describe('setEnv', function() {
  describe('#set', function() {
    it('should be a function', function() {
      assert.equal("function", typeof setEnv.set);
    });
    it('should set the given environment variable', function() {
      setEnv.set('SETENV_TEST');
      assert.equal('SETENV_TEST', process.env['SETENV_TEST']);
    });
    it('should set the given value to the specified environment variable', function() {
      setEnv.set('SETENV_TEST', 'test-value');
      assert.equal('test-value', process.env['SETENV_TEST']);
    });
  });
  
  describe('#verbosity', function() {
    it('should be a boolean', function() {
      assert.equal("boolean", typeof setEnv.verbosity);
    });
    /*it('should output some internal information', function() {
      setEnv.verbosity = true;
      
      assert.doesNotThrow( function() {
        setEnv.set('SETENV_TEST', "bla");
      });
      
      // ToDo: assert if any output was generated
    });*/    
  });
});
